import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, map } from 'rxjs';
import { Request } from './add-user/request';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor(private http: HttpClient) { }

  API = 'http://localhost:8060/InterMove';


  public registerUser(UserData: any) {
    return this.http.post(this.API + '/register', UserData);
  }

  public getUsers() {
    return this.http.get(this.API + '/retrieve-all-users');
  }

  public deleteUsers(id: any) {
    return this.http.delete(this.API + '/remove-user?id=' + id);
  }

  public updateStudents(student: any) {
    return this.http.put(this.API + '/updateUser', student);
  }
  signup(request: Request): Observable<any> {
		return this.http.post<any>(this.API + '/register', request, {headers: new HttpHeaders({ 'Content-Type': 'application/json' }), responseType: 'text' as 'json'}).pipe(map((resp) => {                                                         
			return resp;
		}));
	}
}
