import { Component } from '@angular/core';
import { EventsService } from '../../shared/event.service';
import { OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Event } from '../../model/Event/event.model';

@Component({
  selector: 'app-add-events',
  templateUrl: './add-events.component.html',
  
})
export class AddEventsComponent implements OnInit {

  events: Event = new Event();
  submitted = false;

  constructor(private eventsService: EventsService,
    private router: Router) { }

  ngOnInit() {
  }

  newEmployee(): void {
    this.submitted = false;
    this.events = new Event();
  }

  save() {
    this.eventsService
      .createEvent(this.events)
      .subscribe({
        next: data => {
          console.log(data);
          this.events = new Event();
          this.gotoList();
        },
        error: error => console.log(error)
      });
  }
  

  onSubmit() {
    this.submitted = true;
    this.save();    
  }

  gotoList() {
    this.router.navigate(['/listEvents']);
  }
 
  }
  
<div class="container rounded h-100 p-4"[hidden]="submitted" style="width: 400px; background-color: black;">
    <form (ngSubmit)="onSubmit()" enctype="multipart/form-data">    <label>Title</label>
    <input type="text" class="form-control" id="firstName" required [(ngModel)]="events.title" name="firstName">
    
    <label>Description</label>
    <input type="text" class="form-control" id="firstName" required [(ngModel)]="events.description" name="firstName">

    <label>Start Date</label>
    <input type="date" [(ngModel)]="events.dateD" name="dateF" required>

          <label>End Date</label>
    <input type="date" [(ngModel)]="events.dateF" name="dateF" required>
    
    <label>Number of Places</label>
    <input type="number" [(ngModel)]="events.nbreDePlaces" name="nbreDePlaces" required>
    
    <div class="mb-3">
        <label for="formFile" class="form-label">Organisateur</label>
        <input type="text" class="form-control" id="firstName" required [(ngModel)]="events.image" name="speaker">
    </div>
    <label>Invite </label>
    <input type="text" class="form-control" id="firstName" required [(ngModel)]="events.speaker" name="speaker">

    <label>Type of Event</label>
    <select [(ngModel)]="events.typeEvent" name="typeEvent" required>
        <option>Workshop</option>
        <option>Webinaire</option>

    </select>
    
    <label>Modality of Event</label>
    <select [(ngModel)]="events.modaliteEvent" name="modaliteEvent" required>
        <option>Presentiel</option>
        <option>Distanciel</option>  
    </select>
    
    <button type="submit">Save Event</button>
  </form>
